typeDetail.IButtonGroupData:
interface IButtonGroupData {
  text: string // 显示文本
  value: number | number // 选中的值
  disabled?: boolean // 是否禁用
  // 配置选块角标
  sup?: {
    text: string // 角标文本
    class?: string // 角标的自定义class
    slot?: string // 自定义插槽名称，提供后可在button-group内使用对应的插槽自定义角标内容
    icon?: Component // 传入图标组件
  }
}
